package day13_String;

public class ChangingCases {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String str = "Hello";
		System.out.println(str);
		
		str = str.toLowerCase();
		System.out.println(str);
		
		str = str.toUpperCase();
		System.out.println(str);

	}

}
